import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const ListPelanggan = () => {
  const [pelanggan, setPelanggan] = useState([]);

  useEffect(() => {
    getPelanggan();
  }, []);

  const getPelanggan = async () => {
    const response = await axios.get("http://localhost:5000/pelanggan");
    setPelanggan(response.data);
  };

  const deletePelanggan = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/pelanggan/${id}`);
      getPelanggan();
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="columns mt-5 is-centered">
      <div className="column is-half">
        <Link to={`add`} className="button is-success">
          Add New
        </Link>
        <table className="table is-striped is-fullwidth">
          <thead>
            <tr>
              <th>PelangganID</th>
              <th>NamaPelanggan</th>
              <th>Alamat</th>
              <th>NomorTelepon</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user, index) => (
              <tr key={user.id}>
                <td>{index + 1}</td>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>{user.gender}</td>
                <td>
                  <Link
                    to={`edit/${user.id}`}
                    className="button is-small is-info mr-2"
                  >
                    Edit
                  </Link>
                  <button
                    onClick={() => deletePelanggan(user.id)}
                    className="button is-small is-danger"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ListPelanggan;
